mapper.select:
SELECT B.SEQ /* PmsMapper.selectPmsList 일정관리 목록 조회 */ , B.CODE , (SELECT CODE_NAME FROM CODE A WHERE A.CODE_TYPE = 'P_TYPE' AND A.CODE = B.CODE) AS TYPE , B.MENU_DEPTH_1 , B.MENU_DEPTH_2 , B.MENU_DEPTH_3 , B.MENU_DEPTH_4 , B.PROG_FILE_NM , B.MENU_NAME , B.STATE , (SELECT CODE_NAME FROM CODE A WHERE A.CODE_TYPE = 'P_STATE' AND A.CODE = B.STATE) AS STATE_NM , B.MASTER_NAME , DATE_FORMAT(STR_TO_DATE(B.START_DT, '%Y%m%d'),'%Y-%m-%d ') START_DT , DATE_FORMAT(STR_TO_DATE(B.DUE_DT, '%Y%m%d'),'%Y-%m-%d ') DUE_DT , DATE_FORMAT(STR_TO_DATE(B.FIN_DT, '%Y%m%d'),'%Y-%m-%d ') FIN_DT , DATE_FORMAT(B.REG_DT, '%Y-%m-%d %H:%i:%s') REG_DT , DATE_FORMAT(B.CNG_DT, '%Y-%m-%d %H:%i:%s') CNG_DT FROM PROJECT_MANAGE_PLAN B
AND B.MENU_NAME LIKE CONCAT('%',#{menuName},'%') AND B.MASTER_NAME LIKE CONCAT('%',#{masterName},'%') AND MENU_DEPTH_1 LIKE CONCAT('%',#{menu1Depth},'%')
LIMIT #{pageListCnt} OFFSET #{startIdx}

update.foreach:
UPDATE PROJECT_MANAGE_PLAN /* PmsMapper.updatePmsList PMS 리스트 update */
	SET
		CODE = #{rowData.CODE}
		, MENU_DEPTH_1 = #{rowData.MENU_DEPTH_1}
		, MENU_DEPTH_2 = #{rowData.MENU_DEPTH_2}
		, MENU_DEPTH_3 = #{rowData.MENU_DEPTH_3}
		, MENU_DEPTH_4 = #{rowData.MENU_DEPTH_4}
		, PROG_FILE_NM = #{rowData.PROG_FILE_NM}
		, MENU_NAME = #{rowData.MENU_NAME}
		, STATE = #{rowData.STATE}
		, MASTER_NAME = #{rowData.MASTER_NAME}
		, START_DT = #{rowData.START_DT}
		, DUE_DT = #{rowData.DUE_DT}
		, FIN_DT = #{rowData.FIN_DT}
		, CNG_DT = CURRENT_TIMESTAMP()
	WHERE SEQ = #{rowData.SEQ}

mapper.insert:
INSERT INTO PROJECT_MANAGE_PLAN ( /* PmsMapper.insertPmsList PMS 리스트 insert */

			CODE
			, MENU_DEPTH_1
			, MENU_DEPTH_2
			, MENU_DEPTH_3
			, MENU_DEPTH_4
			, PROG_FILE_NM
			, MENU_NAME
			, STATE
			, MASTER_NAME
			, START_DT
			, DUE_DT
			, FIN_DT
			, REG_DT
			, CNG_DT
		) VALUES

		(

			#{rowData.CODE}
			, #{rowData.MENU_DEPTH_1}
			, #{rowData.MENU_DEPTH_2}
			, #{rowData.MENU_DEPTH_3}
			, #{rowData.MENU_DEPTH_4}
			, #{rowData.PROG_FILE_NM}
			, #{rowData.MENU_NAME}
			, #{rowData.STATE}
			, #{rowData.MASTER_NAME}
			, #{rowData.START_DT}
			, #{rowData.DUE_DT}
			, #{rowData.FIN_DT}
			, CURRENT_TIMESTAMP()
			, CURRENT_TIMESTAMP() 
		)

mapper.delete:
DELETE FROM PROJECT_MANAGE_PLAN /* PmsMapper.deletePmsOne 일정관리 delete */
 WHERE SEQ IN 

	  #{seq}
